mapper.delete:
UPDATE department
SET del_flag = 1
WHERE id = #{id,jdbcType=INTEGER}

mapper.select:
select id,content,name,code,create_time AS createTime,store_id AS storeId,edge_server_id AS edgeServerId from model_log WHERE  store_id = #{id,jdbcType=INTEGER}

  AND create_time >= #{startTime ,jdbcType=VARCHAR}


  AND create_time < #{endTime ,jdbcType=VARCHAR}
