SELECT e.id, e.e_code, e.type, e.name, e.os_code, e.os_version,e.create_time, e.del_flag,e.thumbnail,e.thumbnail_name FROM edge_device e WHERE e.id in
#{item,jdbcType=INTEGER}
AND e.del_flag=0

SELECT e.id, e.e_code, e.type, e.name, e.os_code, e.os_version,e.create_time, e.del_flag,e.thumbnail,e.thumbnail_name, o.id AS osId,o.`name`AS osName,o.type AS osType,o.parameter AS osParameter FROM edge_device e LEFT JOIN operating_system o ON e.os_code=o.os_code WHERE e.id in
#{item,jdbcType=INTEGER}
AND e.del_flag=0

SELECT e.id, e.e_code, e.type, e.name, e.os_code,e.os_version, e.create_time, e.del_flag,d.is_drag FROM edge_device e LEFT JOIN department_device d ON e.id=d.e_id WHERE e.del_flag=0
AND e.type = #{type,jdbcType=VARCHAR}
GROUP BY e.id

SELECT e.id, e.e_code, e.type, e.name, e.os_code, e.os_version,e.create_time, e.del_flag,e.thumbnail_name,e.thumbnail,o.`name`AS osName,o.type AS osType FROM edge_device e LEFT JOIN operating_system o ON e.os_code=o.os_code WHERE e.del_flag=0 AND e.type = #{type,jdbcType=CHAR}
AND e.name LIKE concat('%',#{name},'%')
ORDER BY e.create_time DESC

SELECT id, e_code, type, name, os_code,os_version, create_time, del_flag,thumbnail
FROM edge_device
WHERE thumbnail = #{path,jdbcType=VARCHAR}

SELECT id, e_code, type, name, os_code, os_version, create_time, del_flag,thumbnail FROM edge_device WHERE os_code in
#{item,jdbcType=VARCHAR}
AND del_flag=0
